    <script>
        function selectRoom(roomId, cardElement) {
            // Remove previous selection
            document.querySelectorAll('.room-card').forEach(function(c) {
                c.classList.remove('border', 'border-primary');
                var indicator = c.querySelector('.selected-indicator');
                if (indicator) indicator.style.display = 'none';
            });
            
            // Mark new selection
            cardElement.classList.add('border', 'border-primary');
            var indicator = cardElement.querySelector('.selected-indicator');
            if (indicator) indicator.style.display = 'block';
            
            document.getElementById('selectedRoomId').value = roomId;
            document.getElementById('submitBtn').disabled = false;
            
            // Update summary from data attributes
            var roomName = cardElement.getAttribute('data-room-name');
            var roomType = cardElement.getAttribute('data-room-type'");
                WriteLiteral(@");
            var roomPrice = cardElement.getAttribute('data-room-price');
            
            var html = '<p><strong>Room:</strong> ' + roomName + '</p>';
            html += '<p><strong>Type:</strong> ' + roomType + '</p>';
            html += '<p><strong>Price:</strong> $' + roomPrice + '/night</p>';
            document.getElementById('bookingSummary').innerHTML = html;
        }

        // Date validation
        var checkInInput = document.querySelector('input[name=""BookingDate""]');
        if (checkInInput) {
            checkInInput.addEventListener('change', function() {
                var checkOut = document.querySelector('input[name=""CheckOutDate""]');
                if (checkOut) {
                    checkOut.min = this.value;
                    if (checkOut.value && checkOut.value <= this.value) {
                        checkOut.value = '';
                    }
                }
            });
        }
    </script>
");
            }
